SELECT DISTINCT "distinctAlias"."Transaction_id" as "ids_Transaction_id", "distinctAlias"."Transaction_date", "distinctAlias"."Transaction_id" FROM (SELECT "Transaction"."id" AS "Transaction_id", "Transaction"."type" AS "Transaction_type", "Transaction"."name" AS "Transaction_name", "Transaction"."amount" AS "Transaction_amount", "Transaction"."relatedAmount" AS "Transaction_relatedAmount", "Transaction"."date" AS "Transaction_date", "Transaction"."relatedDate" AS "Transaction_relatedDate", "Transaction"."notes" AS "Transaction_notes", "Transaction"."accountId" AS "Transaction_accountId", "Transaction"."categoryId" AS "Transaction_categoryId", "Transaction"."personId" AS "Transaction_personId", "Transaction"."projectId" AS "Transaction_projectId", "Transaction"."vendorId" AS "Transaction_vendorId", "Transaction__account"."id" AS "Transaction__account_id", "Transaction__account"."name" AS "Transaction__account_name", "Transaction__category"."id" AS "Transaction__category_id", "Transaction__category"."name" AS "Transaction__category_name", "Transaction__category"."type" AS "Transaction__category_type", "Transaction__person"."id" AS "Transaction__person_id", "Transaction__person"."name" AS "Transaction__person_name", "Transaction__project"."id" AS "Transaction__project_id", "Transaction__project"."name" AS "Transaction__project_name", "Transaction__vendor"."id" AS "Transaction__vendor_id", "Transaction__vendor"."name" AS "Transaction__vendor_name", "Transaction__documents"."id" AS "Transaction__documents_id", "Transaction__documents"."name" AS "Transaction__documents_name", "Transaction__documents"."path" AS "Transaction__documents_path", "Transaction__documents"."transactionId" AS "Transaction__documents_transactionId" FROM "transaction" "Transaction" LEFT JOIN "account" "Transaction__account" ON "Transaction__account"."id"="Transaction"."accountId"  LEFT JOIN "category" "Transaction__category" ON "Transaction__category"."id"="Transaction"."categoryId"  LEFT JOIN "person" "Transaction__person" ON "Transaction__person"."id"="Transaction"."personId"  LEFT JOIN "project" "Transaction__project" ON "Transaction__project"."id"="Transaction"."projectId"  LEFT JOIN "vendor" "Transaction__vendor" ON "Transaction__vendor"."id"="Transaction"."vendorId"  LEFT JOIN "document" "Transaction__documents" ON "Transaction__documents"."transactionId"="Transaction"."id"
WHERE ( ("Transaction"."name" ILIKE $1 OR "Transaction__project"."name" ILIKE $2 OR "Transaction__vendor"."name" ILIKE $3 OR "Transaction__account"."name" ILIKE $4 OR "Transaction__category"."name" ILIKE $5)
 AND "Transaction"."accountId" = $6 ) AND ( "Transaction"."deletedAt" IS NULL )) "distinctAlias" ORDER BY "distinctAlias"."Transaction_date" DESC, "distinctAlias"."Transaction_id" DESC, "Transaction_id" ASC LIMIT 10 -- PARAMETERS: ["%a%","%a%","%a%","%a%","%a%",1]
